import { v4 } from 'uuid';
import { SET_ALERT, REMOVE_ALERT } from './types';

//setAlert = () => mapDispatchToProps;
export const setAlert = (msg, alertType, timeout = 5000) => mapDispatchToProps => dispatch => {
    const id = v4();
    dispatch({
        type: SET_ALERT,
        payload: { msg, alertType, id }
    });

    setTimeout(() => dispatch({ type: REMOVE_ALERT, payload: id }), timeout);
}
