import React, { Fragment, useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import Spinner from '../layout/Spinner';
import webSocket from 'socket.io-client';
import Search from './Search';
import Inbox_people from './Inbox_people';

const Chat = () => {
  const [ws, setWs] = useState(null);
  const [msg, setMsg] = useState('');

  const connectWebSocket = () => {
    //開啟
    setWs(webSocket('http://localhost:8888'));
  };

  useEffect(() => {
    ws == null && connectWebSocket();
    if (ws) {
      //連線成功在 console 中打印訊息
      console.log('success connect!');
      console.log('ws id: ' + ws.id);
      //設定監聽
      initWebSocket();
    }
  }, [ws]);

  const initWebSocket = () => {
    //對 getMessage 設定監聽，如果 server 有透過 getMessage 傳送訊息，將會在此被捕捉
    ws.on('getMessage', (message) => {
      console.log(message);
    });
  };

  const sendMessage = () => {
    //以 emit 送訊息，並以 getMessage 為名稱送給 server 捕捉
    ws != null &&
      ws.emit('getMessage', { user: 'Andrew', to: 'Nicole', msg: msg });
    setMsg('');
  };

  return (
    <Fragment>
      <h1 className='large text-primary'>Messaging</h1>
      <div class='container'>
        <div class='messaging'>
          <div class='inbox_msg'>
            <Inbox_people />

            <div class='mesgs'>
              <div class='msg_history'>
                <div class='incoming_msg'>
                  <div class='incoming_msg_img'>
                    {' '}
                    <img
                      src='./index_files/user-profile.png'
                      alt='sunil'
                    />{' '}
                  </div>
                  <div class='received_msg'>
                    <div class='received_withd_msg'>
                      <p>Test which is a new approach to have all solutions</p>
                      <span class='time_date'> 11:01 AM | June 9</span>
                    </div>
                  </div>
                </div>
                <div class='outgoing_msg'>
                  <div class='sent_msg'>
                    <p>Test which is a new approach to have all solutions</p>
                    <span class='time_date'> 11:01 AM | June 9</span>
                  </div>
                </div>
                <div class='incoming_msg'>
                  <div class='incoming_msg_img'>
                    {' '}
                    <img
                      src='./index_files/user-profile.png'
                      alt='sunil'
                    />{' '}
                  </div>
                  <div class='received_msg'>
                    <div class='received_withd_msg'>
                      <p>Test, which is a new approach to have</p>
                      <span class='time_date'> 11:01 AM | Yesterday</span>
                    </div>
                  </div>
                </div>
                <div class='outgoing_msg'>
                  <div class='sent_msg'>
                    <p>Apollo University, Delhi, India Test</p>
                    <span class='time_date'> 11:01 AM | Today</span>
                  </div>
                </div>
                <div class='incoming_msg'>
                  <div class='incoming_msg_img'>
                    {' '}
                    <img
                      src='./index_files/user-profile.png'
                      alt='sunil'
                    />{' '}
                  </div>
                  <div class='received_msg'>
                    <div class='received_withd_msg'>
                      <p>
                        We work directly with our designers and suppliers, and
                        sell direct to you, which means quality, exclusive
                        products, at a price anyone can afford.
                      </p>
                      <span class='time_date'> 11:01 AM | Today</span>
                    </div>
                  </div>
                </div>
              </div>
              <div class='type_msg'>
                <div class='input_msg_write'>
                  <input
                    type='text'
                    class='write_msg'
                    placeholder='Type a message'
                  />
                  <button class='msg_send_btn' type='button'>
                    <i class='fa fa-paper-plane-o' aria-hidden='true'></i>
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default Chat;
