import React, { Fragment, useEffect, useState } from 'react';
import webSocket, { Socket } from 'socket.io-client';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { connectWebSocket } from '../../actions/chat';
import Incoming_msg from './Incoming_msg';
import Outgoing_msg from './Outgoing_msg';

const Mesgs = ({ msgData, loading, connectWebSocket }) => {
  const [ws, setWs] = useState(null);
  const [msg, setMsg] = useState('');

  useEffect(() => {
    if (loading) {
      connectWebSocket();
    }

    if (ws) {
      //連線成功在 console 中打印訊息
      console.log('success connect!');
      console.log('ws id: ' + ws.id);
      //設定監聽
      initWebSocket();
    }
    return () => {
      console.log('close');
    };
  }, [ws]);

  const initWebSocket = () => {
    //對 getMessage 設定監聽，如果 server 有透過 getMessage 傳送訊息，將會在此被捕捉
    ws.on('getMessage', (data) => {
      //setmsgData([{ msg: data.msg, time: data.time }, ...msgData]);
      //console.log(data.from, ': ', data.msg, msgData.length);
    });
  };

  const sendMessage = () => {
    const data = {
      from: 'Andrew',
      to: 'Nicole',
      time: new Date(),
      msg: msg,
    };

    //以 emit 送訊息，並以 getMessage 為名稱送給 server 捕捉
    ws != null && ws.emit('getMessage', data);
    setMsg('');
  };

  return (
    <div class='mesgs'>
      <div class='msg_history'>
        {/*  */}
        <Incoming_msg msg='test msg' />
        <Outgoing_msg />
        {msgData.map((data) => (
          <Incoming_msg msg={data.msg} key={data.time} />
        ))}

        {/*  */}
      </div>

      <div class='type_msg'>
        <div class='input_msg_write'>
          {/* Input massage */}
          <input
            type='text'
            class='write_msg'
            value={msg}
            onChange={(e) => {
              setMsg(e.target.value);
            }}
            placeholder='Type a message'
          />

          <button class='msg_send_btn' type='button' onClick={sendMessage}>
            <i class='fa fa-paper-plane-o' aria-hidden='true'></i>
          </button>
        </div>
      </div>
    </div>
  );
};

Mesgs.prototypes = {
  msgData: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  socket: state.chat.socket,
  msgData: state.chat.msgData,
  loading: state.chat.loading,
});

export default connect(mapStateToProps, { connectWebSocket })(Mesgs);
