import React, { Fragment, useEffect, useState } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

const Chat_list = ({ nowTalkingTo, userId, name, avatar }) => {
  const onclick = (e) => {
    //TO DO: 要調action用方法更新msgData nowTalingTo active_chat
    console.log(e.target.name);
  };

  //判斷是否要將此用戶標記為正在與他聊天

  return (
    <div
      className={`chat_list ${nowTalkingTo === userId ? 'active_chat' : ''}`}
      onClick={(e) => onclick(e)}
    >
      <div className='chat_people'>
        <div className='chat_img'>
          {' '}
          <img src={avatar} alt={name} />{' '}
        </div>
        <div className='chat_ib'>
          <h5>
            {name} <span className='chat_date'>Dec 25</span>
          </h5>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({
  nowTalkingTo: state.chat.nowTalkingTo,
});

export default connect(mapStateToProps, {})(Chat_list);
