import React, { Fragment, useEffect, useState } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import Search from './Search';
import { getFriendsProfiles } from '../../actions/profile';
import Chat_list from './Chat_list';
import Spinner from '../layout/Spinner';
import Moment from 'react-moment';
import moment from 'moment';
const Inbox_people = ({
  profile: { friends, loading },
  chat_loading,
  getFriendsProfiles,
}) => {
  //要讀取所有朋友資料
  useEffect(() => {
    getFriendsProfiles();
  }, [getFriendsProfiles]);

  return (
    <div class='inbox_people'>
      <Search />
      <div class='inbox_chat'>
        {loading || chat_loading ? (
          <Spinner />
        ) : friends.length > 0 ? (
          friends.map((friend) => (
            <Chat_list
              key={friend.user._id}
              userId={friend.user._id}
              name={friend.user.name}
              avatar={friend.user.avatar}
            />
          ))
        ) : (
          <span>No friends found...</span>
        )}
      </div>
    </div>
  );
};

Inbox_people.propTypes = {
  profile: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  profile: state.profile,
  chat_loading: state.chat.loading,
});

export default connect(mapStateToProps, { getFriendsProfiles })(Inbox_people);
