import React, { Fragment, useEffect, useState } from 'react';
import webSocket, { Socket } from 'socket.io-client';
import Incoming_msg from './Incoming_msg';
import Outgoing_msg from './Outgoing_msg';

const Mesgs = () => {
  const [ws, setWs] = useState(null);
  const [msg, setMsg] = useState('');

  const [msgData, setmsgData] = useState([]);

  const connectWebSocket = () => {
    //開啟
    setWs(webSocket('http://localhost:8888'));
  };

  useEffect(() => {
    ws == null && connectWebSocket();
    if (ws) {
      //連線成功在 console 中打印訊息
      console.log('success connect!');
      console.log('ws id: ' + ws.id);
      //設定監聽
      initWebSocket();
    }
    return () => {
      console.log('close');
    };
  }, [ws]);

  const initWebSocket = () => {
    //對 getMessage 設定監聽，如果 server 有透過 getMessage 傳送訊息，將會在此被捕捉
    ws.on('getMessage', (data) => {
      setmsgData([
        { msg: data.msg, time: data.time.toLocaleTimeString() },
        ...msgData,
      ]);

      console.log(data.from, ': ', data.msg, msgData.length);
    });
  };

  const updateMsgData = () => {
    setmsgData([{ msg: 'test' }, ...msgData]);
    console.log('array length is: ', msgData.length);
    console.log(msgData);
  };

  const sendMessage = () => {
    const data = {
      from: 'Andrew',
      to: 'Nicole',
      time: new Date(),
      msg: msg,
    };

    //以 emit 送訊息，並以 getMessage 為名稱送給 server 捕捉
    ws != null && ws.emit('getMessage', data);
    setMsg('');
  };

  return (
    <div class='mesgs'>
      <div class='msg_history'>
        {/*  */}
        <Incoming_msg msg='test msg' />
        <Outgoing_msg />
        {msgData.map((data) => (
          <Incoming_msg msg={data.msg} key={data.time} />
        ))}

        {/*  */}
      </div>

      <div class='type_msg'>
        <div class='input_msg_write'>
          {/* Input massage */}
          <input
            type='text'
            class='write_msg'
            value={msg}
            onChange={(e) => {
              setMsg(e.target.value);
            }}
            placeholder='Type a message'
          />

          <button class='msg_send_btn' type='button' onClick={sendMessage}>
            <i class='fa fa-paper-plane-o' aria-hidden='true'></i>
          </button>
          <button onClick={updateMsgData}>test</button>
        </div>
      </div>
    </div>
  );
};

export default Mesgs;
