import {
  UPDATE_MESSAGE,
  CONNECT_WEBSOCKET,
  DISCONNECT_WEBSOCKET,
} from '../actions/types';

const initialState = {
  socket: null,
  msg: [],
  friend: null,
  loading: true,
};

export default function (state = initialState, action) {
  const { type, payload } = action;
  switch (type) {
    case CONNECT_WEBSOCKET:
      return {
        ...state,
        socket: payload,
        loading: false,
      };
    case UPDATE_MESSAGE:
      return {
        ...state,
        msg: [payload, ...state.posts],
        loading: false,
      };

    case DISCONNECT_WEBSOCKET:
      return {
        socket: null,
        msg: [],
        loading: true,
      };
    default:
      return state;
  }
}
