import webSocket, { Socket } from 'socket.io-client';
import { CONNECT_WEBSOCKET, UPDATE_MESSAGE, SET_TALKER } from './types';
import store from '../store';
export const connectWebSocket = (userId) => async (dispatch) => {
  try {
    let socketId = '';
    const ws = await webSocket('http://localhost:8888');

    console.log('waiting for the message...');

    ws.on('connect', () => {
      socketId = ws.id;
      console.log('This is my websokcet objects id: ', ws.id);
      //Send User ID and user webSocket ID
      ws.emit('user_login', {
        userId,
        socketId,
      });
    });

    ws.on('getMessage', (data) => {
      const state = store.getState();
      const nowTalkingTo = state.chat.nowTalkingTo;
      console.log('now talking to is: ', nowTalkingTo);
      console.log('From: ', data.from, 'Msg: ', data.msg);
      //更新現在聊天對像的msgData
      dispatch({
        type: UPDATE_MESSAGE,
        payload: Object.assign({}, data),
      });

      //TO DO: 還要更新allMsgData裡的連天資料

      //需要nowTalkingTo，判斷是否等於from，等於就要同時更新msaData和allMsgData
    });

    dispatch({ type: CONNECT_WEBSOCKET, payload: ws });
  } catch (error) {
    console.log(error);
  }
};

export const sendMessage = (socket, data) => async (dispatch) => {
  try {
    socket != null && socket.emit('getMessage', data);
    dispatch({
      type: UPDATE_MESSAGE,
      payload: Object.assign({}, data),
    });
  } catch (error) {
    console.log(error);
  }
};

//設定要連天的對象，去allMsgData裡拿出對應user id 的聊天資料，並將此資料設定為msgData
export const setTalker = (userId, allMsgData) => async (dispatch) => {
  const msgData = allMsgData.get(userId) ? allMsgData.get(userId) : [];

  dispatch({
    type: SET_TALKER,
    payload: { msgData, userId },
  });
};
