import React, { Fragment, useEffect, useState } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import Search from './Search';
import { getFriendsProfiles } from '../../actions/profile';
import Chat_list from './Chat_list';
import Spinner from '../layout/Spinner';
const Inbox_people = ({
  profile: { friends, loading },
  getFriendsProfiles,
}) => {
  //要讀取所有朋友資料
  useEffect(() => {
    getFriendsProfiles();
  }, [getFriendsProfiles]);

  return (
    <div class='inbox_people'>
      <Search />
      <div class='inbox_chat'>
        {loading ? (
          <Spinner />
        ) : friends.length > 0 ? (
          friends.map((friend) => (
            <Chat_list
              key={friend.user._id}
              name={friend.user.name}
              avatar={friend.user.avatar}
            />
          ))
        ) : (
          <span>No friends found...</span>
        )}

        <div class='chat_list'>
          <div class='chat_people'>
            <div class='chat_img'>
              {' '}
              <img src='./index_files/user-profile.png' alt='sunil' />{' '}
            </div>
            <div class='chat_ib'>
              <h5>
                Sunil Rajput <span class='chat_date'>Dec 25</span>
              </h5>
              <p>
                Test, which is a new approach to have all solutions astrology
                under one roof.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

Inbox_people.propTypes = {
  profile: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  profile: state.profile,
});

export default connect(mapStateToProps, { getFriendsProfiles })(Inbox_people);
